public class FooTest
{
    [Test]
    public void MyTest()
    {
        var foo1 = new Foo() { Value = 123 };    
        var foo2 = new Foo() { Value = 123 };

        Assert.AreEqual(foo1, foo2, new StructuralEqualityComparer<Foo>
        {
            { x => x.Value, new MyComparer() },
        });
    }
}

